import { Switch, Route, useLocation } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { SidebarProvider, SidebarTrigger } from "@/components/ui/sidebar";
import { AppSidebar } from "@/components/app-sidebar";
import { ProtectedRoute } from "@/components/protected-route";
import { CitizenLayout } from "@/components/citizen-layout";
import { getCurrentUser } from "@/lib/auth";

import NotFound from "@/pages/not-found";
import Login from "@/pages/login";
import Landing from "@/pages/landing";
import BusinessRegister from "@/pages/business-register";
import CitizenPortal from "@/pages/citizen-portal";
import Dashboard from "@/pages/dashboard";
import RevenueSources from "@/pages/revenue-sources";
import Invoices from "@/pages/invoices";
import Payments from "@/pages/payments";
import Taxpayers from "@/pages/taxpayers";
import Reports from "@/pages/reports";
import Settings from "@/pages/settings";
import PaymentFlow from "@/pages/payment-flow";
import Verify from "@/pages/verify";

function AdminLayout({ children }: { children: React.ReactNode }) {
  return (
    <div className="flex h-screen w-full">
      <AppSidebar />
      <div className="flex flex-col flex-1 overflow-hidden">
        <header className="flex items-center gap-2 p-4 border-b bg-card">
          <SidebarTrigger data-testid="button-sidebar-toggle" />
        </header>
        <main className="flex-1 overflow-auto">
          {children}
        </main>
      </div>
    </div>
  );
}

function Router() {
  const [location] = useLocation();
  const currentUser = getCurrentUser();

  const publicRoutes = ["/", "/login", "/verify", "/business/register"];
  const isPublicRoute = publicRoutes.includes(location);

  if (!currentUser && !isPublicRoute) {
    return <Login />;
  }

  const isAdminRoute = location.startsWith("/dashboard") || 
                       location.startsWith("/revenue-sources") || 
                       location.startsWith("/invoices") || 
                       location.startsWith("/payments") || 
                       location.startsWith("/taxpayers") || 
                       location.startsWith("/reports") || 
                       location.startsWith("/settings");

  const adminRoutes = (
    <ProtectedRoute allowedRoles={["admin", "finance_officer", "auditor"]}>
      <AdminLayout>
        <Switch>
          <Route path="/dashboard" component={Dashboard} />
          <Route path="/revenue-sources">
            <ProtectedRoute allowedRoles={["admin", "finance_officer"]}>
              <RevenueSources />
            </ProtectedRoute>
          </Route>
          <Route path="/invoices">
            <ProtectedRoute allowedRoles={["admin", "finance_officer"]}>
              <Invoices />
            </ProtectedRoute>
          </Route>
          <Route path="/payments" component={Payments} />
          <Route path="/taxpayers">
            <ProtectedRoute allowedRoles={["admin", "finance_officer"]}>
              <Taxpayers />
            </ProtectedRoute>
          </Route>
          <Route path="/reports" component={Reports} />
          <Route path="/settings">
            <ProtectedRoute allowedRoles={["admin"]}>
              <Settings />
            </ProtectedRoute>
          </Route>
        </Switch>
      </AdminLayout>
    </ProtectedRoute>
  );

  if (isAdminRoute && currentUser && ["admin", "finance_officer", "auditor"].includes(currentUser.role)) {
    return adminRoutes;
  }

  return (
    <Switch>
      <Route path="/" component={Landing} />
      <Route path="/login" component={Login} />
      <Route path="/verify" component={Verify} />
      <Route path="/business/register" component={BusinessRegister} />
      <Route path="/citizen">
        <ProtectedRoute allowedRoles={["citizen"]}>
          <CitizenPortal />
        </ProtectedRoute>
      </Route>
      <Route path="/citizen/payment">
        <ProtectedRoute allowedRoles={["citizen"]}>
          <CitizenLayout>
            <PaymentFlow />
          </CitizenLayout>
        </ProtectedRoute>
      </Route>
      <Route path="/citizen/invoices">
        <ProtectedRoute allowedRoles={["citizen"]}>
          <CitizenLayout>
            <Invoices />
          </CitizenLayout>
        </ProtectedRoute>
      </Route>
      <Route path="/citizen/history">
        <ProtectedRoute allowedRoles={["citizen"]}>
          <CitizenLayout>
            <Payments />
          </CitizenLayout>
        </ProtectedRoute>
      </Route>
      {adminRoutes}
      <Route component={NotFound} />
    </Switch>
  );
}

export default function App() {
  const style = {
    "--sidebar-width": "16rem",
    "--sidebar-width-icon": "3rem",
  };

  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <SidebarProvider style={style as React.CSSProperties}>
          <Router />
        </SidebarProvider>
        <Toaster />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

client/src/components/protected-route.ts.content:
import { useEffect } from "react";
import { useLocation } from "wouter";
import { getCurrentUser } from "@/lib/auth";

interface ProtectedRouteProps {
  children: React.ReactNode;
  allowedRoles?: string[];
}

export function ProtectedRoute({ children, allowedRoles }: ProtectedRouteProps) {
  const [, setLocation] = useLocation();
  const currentUser = getCurrentUser();

  useEffect(() => {
    if (!currentUser) {
      setLocation("/");
      return;
    }

    if (allowedRoles && !allowedRoles.includes(currentUser.role)) {
      if (currentUser.role === "citizen") {
        setLocation("/citizen");
      } else {
        setLocation("/dashboard");
      }
    }
  }, [currentUser, allowedRoles, setLocation]);

  if (!currentUser) {
    return null;
  }

  if (allowedRoles && !allowedRoles.includes(currentUser.role)) {
    return null;
  }

  return <>{children}</>;
}

client/src/components/ui/toggle-group.ts.content:
"use client"

import * as React from "react"
import * as ToggleGroupPrimitive from "@radix-ui/react-toggle-group"
import { type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"
import { toggleVariants } from "@/components/ui/toggle"

const ToggleGroupContext = React.createContext<
  VariantProps<typeof toggleVariants>
>({
  size: "default",
  variant: "default",
})

const ToggleGroup = React.forwardRef<
  React.ElementRef<typeof ToggleGroupPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof ToggleGroupPrimitive.Root> &
    VariantProps<typeof toggleVariants>
>(({ className, variant, size, children, ...props }, ref) => (
  <ToggleGroupPrimitive.Root
    ref={ref}
    className={cn("flex items-center justify-center gap-1", className)}
    {...props}
  >
    <ToggleGroupContext.Provider value={{ variant, size }}>
      {children}
    </ToggleGroupContext.Provider>
  </ToggleGroupPrimitive.Root>
))

ToggleGroup.displayName = ToggleGroupPrimitive.Root.displayName

const ToggleGroupItem = React.forwardRef<
  React.ElementRef<typeof ToggleGroupPrimitive.Item>,
  React.ComponentPropsWithoutRef<typeof ToggleGroupPrimitive.Item> &
    VariantProps<typeof toggleVariants>
>(({ className, children, variant, size, ...props }, ref) => {
  const context = React.useContext(ToggleGroupContext)

  return (
    <ToggleGroupPrimitive.Item
      ref={ref}
      className={cn(
        toggleVariants({
          variant: context.variant || variant,
          size: context.size || size,
        }),
        className
      )}
      {...props}
    >
      {children}
    </ToggleGroupPrimitive.Item>
  )
})

ToggleGroupItem.displayName = ToggleGroupPrimitive.Item.displayName

export { ToggleGroup, ToggleGroupItem }

